import { FormControl, Grid, InputLabel, TextField, withStyles,Select, MenuItem } from '@material-ui/core'
import React,{useState,useEffect} from 'react'
import { useSyncExternalStore } from 'react'
/*import useForm from './useForm'*/

const styles  = theme => ({
  root:{
      '& .MuiTextField-root':{
        margin : theme.spacing(1),
        minWidth:230
      }
  },
  formControl:{
    margin : theme.spacing(1),
    minWidth:230
  }
})

const initialFieldValues = {
  fullName : '',
  mobile : '',
  email : '',
  age : '',
  bloodGroup : '',
  address:''
}



const DCandidateForm = ({classes,...props}) => {  


   const inputLabel = React.useRef(null)
   const [labelWidth,setLabelWidth] = useState(0)
   useEffect(()=>{
    setLabelWidth(inputLabel.current.offsetWidth)
   },[])

   /*const {
    values,
    setValues,
    handleInputChange
  } = useForm(initialFieldValues)*/
  const [values, setValues] = useSyncExternalStoreState[initialFieldValues]

  const handleInputChange = (e) => {
    setValues((prev) => {
      let helper = {...prev}

      helper['${e.target.id}'] = e.target.value
    })
  }


  return (
    <form autoComplete='off' noValidate className={classes.root}>
      <Grid container>
        <Grid item xs={6}>
          <TextField
               name="fullName"
               id="fullName"
               variant='outlined'
               label='Full Name'
               value={values.fullName}
               onChange={handleInputChange}></TextField>
          <TextField
               name="email"
               id="email"
               variant='outlined'
               label='EMail'
               value={values.email}
               onChange={handleInputChange}></TextField>
           <FormControl variant='outlined' className={classes.formControl}>
              <InputLabel ref={inputLabel} >Blood Group</InputLabel>
              <Select
                  name="bloodGroup"
                  id="bloodGroup"
                  value={values.bloodGroup}
                  onChange={handleInputChange}
                  labelWidth={labelWidth}
                  >
                  <MenuItem defaultValue="">Select Blood Group</MenuItem>
                  <MenuItem value="A+">A +ve</MenuItem>
                  <MenuItem value="A-">A -ve</MenuItem>
                  <MenuItem value="B+">B +ve</MenuItem>
                  <MenuItem value="B-">B -ve</MenuItem>
                  <MenuItem value="O+">O +ve</MenuItem>
                  <MenuItem value="O-">O -ve</MenuItem>
              </Select>
           </FormControl>
        </Grid>

        <Grid item xs={6}>
          <TextField
               name="mobile"
               id="mobile"
               variant='outlined'
               label='Mobile'
               value={values.mobile}
               onChange={handleInputChange}></TextField>
          <TextField
               name="age"
               id="age"
               variant='outlined'
               label='Age'
               value={values.age}
               onChange={handleInputChange}></TextField>
          <TextField
               name="address"
               variant='outlined'
               id="address"
               label='Address'
               value={values.address}
               onChange={handleInputChange}></TextField>
        </Grid>

      </Grid>
    </form>
  )
}

export default withStyles(styles)(DCandidateForm)
