import { FormControl, Grid, InputLabel, TextField, withStyles,Select, MenuItem, Button } from '@material-ui/core'
import React,{useState,useEffect} from 'react'
import { useSyncExternalStore } from 'react'
import useForm from './useForm'

const styles  = theme => ({
  root:{
      '& .MuiTextField-root':{
        margin : theme.spacing(1),
        minWidth:230
      }
  },
  formControl:{
    margin : theme.spacing(1),
    minWidth:230
  },
  smMargin:{
    margin : theme.spacing(1)
  }
})

const initialFieldValues = {
  fullName : '',
  mobile : '',
  email : '',
  age : '',
  bloodGroup : '',
  address:''
}



const DCandidateForm = ({classes,...props}) => {  


   const inputLabel = React.useRef(null)
   const [labelWidth,setLabelWidth] = useState(0)
   useEffect(()=>{
    setLabelWidth(inputLabel.current.offsetWidth)
   },[])

   const validate = () =>{
      let temp = {}
      temp.fullName = values.fullName?"":"This field is required"
      temp.mobile = values.mobile?"":"This field is required"
      temp.bloodGroup = values.bloodGroup?"":"This field is required"
      temp.email = (/^$|.+@.+..+/).test(values.email)?"":"Email is not valid"

      setErrors({
        ...temp
      })

      return Object.values(temp).every(x=>x=="")
   }
   
   const {
    values,
    setValues,
    errors,
    setErrors,
    handleInputChange
  } = useForm(initialFieldValues)
  
  const handleSubmit = e => {
    e.preventDefault()
    if (validate())
    {
       window.alert('validation succeeded')
    }
  }

  return (
    <form autoComplete='off' noValidate className={classes.root} onSubmit={handleSubmit}>
      <Grid container>
        <Grid item xs={6}>
          <TextField
               name="fullName"              
               variant='outlined'
               label='Full Name'
               value={values.fullName}
               onChange={handleInputChange}
               error = {true}
               helperText={error.fullName}
               ></TextField>
          <TextField
               name="email"               
               variant='outlined'
               label='EMail'
               value={values.email}
               onChange={handleInputChange}
               
               ></TextField>
           <FormControl variant='outlined' className={classes.formControl}>
              <InputLabel ref={inputLabel} >Blood Group</InputLabel>
              <Select
                  name="bloodGroup"                  
                  value={values.bloodGroup}
                  onChange={handleInputChange}
                  labelWidth={labelWidth}
                  error = {true}
               helperText={error.email}
                  >
                  <MenuItem defaultValue="">Select Blood Group</MenuItem>
                  <MenuItem value="A+">A +ve</MenuItem>
                  <MenuItem value="A-">A -ve</MenuItem>
                  <MenuItem value="B+">B +ve</MenuItem>
                  <MenuItem value="B-">B -ve</MenuItem>
                  <MenuItem value="O+">O +ve</MenuItem>
                  <MenuItem value="O-">O -ve</MenuItem>
              </Select>
           </FormControl>
        </Grid>

        <Grid item xs={6}>
          <TextField
               name="mobile"               
               variant='outlined'
               label='Mobile'
               value={values.mobile}
               onChange={handleInputChange}></TextField>
          <TextField
               name="age"               
               variant='outlined'
               label='Age'
               value={values.age}
               onChange={handleInputChange}></TextField>
          <TextField
               name="address"
               variant='outlined'               
               label='Address'
               value={values.address}
               onChange={handleInputChange}></TextField>
          <div>
              <Button
                variant="contained"
                type="submit"
                color="primary"
                className={classes.smMargin}>
                Submit
              </Button>
              <Button
                 variant="contained"                 
                 className={classes.smMargin}>
                Reset
              </Button>
          </div>
        </Grid>
        
      </Grid>
    </form>
  )
}

export default withStyles(styles)(DCandidateForm)
