import React,{useState, useEffect} from 'react'


const useForm = (initialFieldValues) =>{

    

    const handleInputChange = e => {
        
        const {values, setValues} = useState(initialFieldValues)
        const {name,value} = e.target

        setValues({
           ...values,
           [name]: value
         })   
    }
    
    return {
        values,
        setValues,
        handleInputChange
    }
}

export default useForm
