import React, { useEffect,useState } from 'react'
import {connect} from 'react-redux'
import * as actions from '../actions/dCandidate'
import { Container, Grid,Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, withStyles } from '@material-ui/core'
import DCandidateForm from './DCandidateForm'

//root is used to overright the child properties
const styles = theme => ({
  root :{
    
  },
   paper:{
     margin : theme.spacing(2),
     padding : theme.spacing(2)
   }
})

const DCandidates = ({classes,...props}) => {
  
  //useEffect runs when the page/component loads
  useEffect(()=>{
    props.fetchAllCandidates()
  },[]) 


  return (
    <Paper className={classes.paper} elevation={3}>
      <Grid container>
          <Grid item xs={6}>
            <DCandidateForm></DCandidateForm>
          </Grid>
          <Grid item xs={6}>
            <TableContainer>
              <Table>
                  <TableHead>
                      <TableRow>
                          <TableCell>Name</TableCell>
                          <TableCell>Mobile</TableCell>
                          <TableCell>Blood Group</TableCell>
                      </TableRow>
                  </TableHead>
                  <TableBody>
                     {
                       props.dCandidateList.map((record,index) => {
                         return(
                          <TableRow key={index}>
                              <TableCell>{record.fullName}</TableCell>
                              <TableCell>{record.mobile}</TableCell>
                              <TableCell>{record.email}</TableCell>
                          </TableRow>
                         )
                       })
                     }
                  </TableBody>
              </Table>
            </TableContainer>
          </Grid>
      </Grid>
    </Paper>
  )
}

const mapStateToProps = state => ({
  dCandidateList : state.dCandidate.list
})

const mapActionsToProps = {
  fetchAllCandidates : actions.fetchall
}

export default connect(mapStateToProps,mapActionsToProps)(withStyles(styles)(DCandidates))
