import { FormControl, Grid, InputLabel, TextField, withStyles,Select, MenuItem, Button, FormHelperText } from '@material-ui/core'
import React,{useState,useEffect} from 'react'
import { useSyncExternalStore } from 'react'
import useForm from './useForm'
import { ErrorOutlineSharp } from '@material-ui/icons'
import {connect} from 'react-redux'
import * as actions from '../actions/dCandidate'

const styles  = theme => ({
  root:{
      '& .MuiTextField-root':{
        margin : theme.spacing(1),
        minWidth:230
      }
  },
  formControl:{
    margin : theme.spacing(1),
    minWidth:230
  },
  smMargin:{
    margin : theme.spacing(1)
  }
})

const initialFieldValues = {
  fullName : '',
  mobile : '',
  email : '',
  age : '',
  bloodGroup : '',
  address:''
}



const DCandidateForm = ({classes,...props}) => {  


   const inputLabel = React.useRef(null)
   const [labelWidth,setLabelWidth] = useState(0)
   useEffect(()=>{
    setLabelWidth(inputLabel.current.offsetWidth)
   },[])

   const validate = (fieldValues = values) =>{
      let temp = {...errors}
      if ('fullName' in fieldValues)
        temp.fullName = fieldValues.fullName?"":"This field is required"
      if ('mobile' in fieldValues)
        temp.mobile = fieldValues.mobile?"":"This field is required"
      if ('bloodGroup' in fieldValues)
        temp.bloodGroup = fieldValues.bloodGroup?"":"This field is required"
      if ('email' in fieldValues)
        temp.email = (/^$|.+@.+..+/).test(fieldValues.email)?"":"Email is not valid"

      setErrors({
        ...temp
      })

      if (fieldValues == values)
        return Object.values(temp).every(x=>x=="")
   }
   
   const {
    values,
    setValues,
    errors,
    setErrors,
    handleInputChange,
    resetForm
  } = useForm(initialFieldValues, validate, props.setCurrentId)
  
  const handleSubmit = e => {
    e.preventDefault()
    if (validate())
    {
       if (props.currentId == 0 )
        props.createDCandidate(values,()=>window.alert('inserted'))
       else
        props.updateDCandidate(props.currentId,values,()=>window.alert('updated'))
      
    }
    resetForm
  }

  useEffect(() =>{
     if (props.currentId != 0){
      setValues({
        ...props.dCandidateList.find(x=> x.id == props.currentId)
      })
      setErrors({})
     }
      
  },[props.currentId])

  return (
    <form autoComplete='off' noValidate className={classes.root} onSubmit={handleSubmit}>
      <Grid container>
        <Grid item xs={6}>
          <TextField
               name="fullName"              
               variant='outlined'
               label='Full Name'
               value={values.fullName}
               onChange={handleInputChange}
               {...(errors.fullName && {error:true,helperText:errors.fullName})}
               ></TextField>
          <TextField
               name="email"               
               variant='outlined'
               label='EMail'
               value={values.email}
               onChange={handleInputChange}
               {...(errors.email && {error:true,helperText:errors.email})}
               ></TextField>
           <FormControl variant='outlined' className={classes.formControl}
               {...(errors.bloodGroup && {error:true})}>
              <InputLabel ref={inputLabel} >Blood Group</InputLabel>
              <Select
                  name="bloodGroup"                  
                  value={values.bloodGroup}
                  onChange={handleInputChange}
                  labelWidth={labelWidth}
                  
                  >
                  <MenuItem defaultValue="">Select Blood Group</MenuItem>
                  <MenuItem value="A+">A +ve</MenuItem>
                  <MenuItem value="A-">A -ve</MenuItem>
                  <MenuItem value="B+">B +ve</MenuItem>
                  <MenuItem value="B-">B -ve</MenuItem>
                  <MenuItem value="O+">O +ve</MenuItem>
                  <MenuItem value="O-">O -ve</MenuItem>
              </Select>
              {errors.bloodGroup && <FormHelperText>{errors.bloodGroup}</FormHelperText>}
           </FormControl>
        </Grid>

        <Grid item xs={6}>
          <TextField
               name="mobile"               
               variant='outlined'
               label='Mobile'
               value={values.mobile}
               onChange={handleInputChange}
               
               {...(errors.mobile && {error:true,helperText:errors.mobile})}
               ></TextField>
          <TextField
               name="age"               
               variant='outlined'
               label='Age'
               value={values.age}
               onChange={handleInputChange}></TextField>
          <TextField
               name="address"
               variant='outlined'               
               label='Address'
               value={values.address}
               onChange={handleInputChange}></TextField>
          <div>
              <Button
                variant="contained"
                type="submit"
                color="primary"
                className={classes.smMargin}>
                Submit
              </Button>
              <Button
                 variant="contained"                 
                 className={classes.smMargin}
                 onClick={resetForm}>
                Reset
              </Button>
          </div>
        </Grid>
        
      </Grid>
    </form>
  )
}

const mapStateToProps = state => ({
  dCandidateList : state.dCandidate.list
})

const mapActionsToProps = {
  createDCandidate : actions.create,
  updateDCandidate : actions.update
}

export default connect(mapStateToProps,mapActionsToProps)(withStyles(styles)(DCandidateForm))
